import React from 'react';
import './stats-table.scss'
class statsTable extends React.Component{
    constructor(){
        super();
        this.state={
            sortedby : '',
            itemPerpage: 10
        } 
    }
    setPagenumber(e){
        this.setState({itemPerpage : parseInt(e.target.value)})
        }
    render(){
    
        var noOfpages = this.props.data.length/10;
        console.log(this.props.data.length/10)
        var pageNum = [];
        for(let i = 0; i<noOfpages;i++){
            pageNum.push(<li className="pagenum" key={i}>
            {i+1}
        </li>)
        }
        return(
            <section className="table-stats">
            <table cellSpacing="0">
              <tr className="">
                    <td className="index">&nbsp;&nbsp;</td>
                    <td className="tableh coun">Country   <span> <i class="fas fa-sort"></i></span></td>
                    <td className="tableh">Corona   <span> <i class="fas fa-sort"></i></span><br></br> Cases</td>
                    <td className="tableh">Deaths  <span> <i class="fas fa-sort"></i></span></td>
                    <td className="tableh">Recovered  <span> <i class="fas fa-sort"></i></span></td>
                </tr>
              
                    {this.props.data.slice(0 , 10).map((country, index) =><tr key={index}>
                        <td>{index}</td>
                        <td>{country.Country}</td>
                        <td className="blue">{country.TotalConfirmed}</td>
                        <td  className="red">{country.TotalDeaths}</td>
                        <td  className="green">{country.TotalRecovered}</td>
                    </tr>)}
               
            </table>
                    <ul className="pages-list">
                        <li className="pagenum"><i class="fas fa-angle-left"></i></li>
                    {pageNum}
                    <li className="pagenum"><i class="fas fa-angle-right"></i></li>
                    <li>
                        <select id="pagenumbers" onChange={this.setPagenumber}>
                            <option value="10">10/Page</option>
                            <option value="20">20/Page</option>
                            <option value="50">50/Page</option>
                            <option value="100">100/Page</option>
                        </select>
                    </li>
                </ul>
            </section>
        )
    }  
}

export default statsTable;
