import React from 'react';
import './asticle.comp.style.scss';
import POST_DATA from '../posts and social/postdata';
const Articlen = (match)=>{
    console.log(match.match.params.articleid);
    const id = match.match.params.articleid - 1;
    return(  
        <section className="newsletter">
            <p></p> 
            
            <article className="">
                <img alt={POST_DATA[id].imgAlt} src={POST_DATA[id].imgUrl}/>
                <div className="post-contents">
                <h2 className="post-title">{POST_DATA[id].title}</h2>
                <p className="post-shortdes">{POST_DATA[id].text}...</p>
                <a href="#">Learn More...</a>
                </div>
            </article>
        </section>  
   
    
    )
}
export default Articlen;
