import React, { useState, memo  }from "react";
import ReactTooltip from "react-tooltip";
//import MapChart from "./map.comp";
import './map.comp.style.scss';
import {
  ZoomableGroup,
  ComposableMap,
  Geographies,
  Geography
} from "react-simple-maps";

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

  const max=(data)=>{
    let maxi=-1
    data.foreach(e=>{
      if(e>maxi) maxi=e;
    })
    return maxi;
  }
  const min=(data)=>{
    let mini=-1
    data.foreach(e=>{
      if(e<mini) mini=e;
    })
    return mini;
}







const MapChart = ( setTooltipContent ,props ) => {
  return (
    
      <ComposableMap data-tip="" projectionConfig={{ scale: 200 }}>
        <ZoomableGroup> {console.log(props)}
          <Geographies geography={geoUrl}>
            {({ geographies }) =>
              geographies.map(geo => {
               var value  , mini=min(props), maxi=max(props);
               for(let e of props){
                 if(e.Country = geo.properties.NAME){
                  value = e.TotalConfirmed;
                  break; }
                 }
               
               return (<Geography
                  key={geo.rsmKey} data-try="yoy"
                  geography={geo}
                  onMouseEnter={() => {
                    const { NAME} = geo.properties;
                    setTooltipContent(`${NAME} — ${value}`);
                  }}
                  onMouseLeave={() => {
                    setTooltipContent("");
                  }}
                  style={{
                    default: {
                      fill: "#D6D6DA",
                      outline: "none"
                    },
                    hover: {
                      fill: "#F53",
                      outline: "none"
                    },
                    pressed: {
                      fill: "#E42",
                      outline: "none"
                    }
                  }}
                /> )
              })
            }
          </Geographies>
        </ZoomableGroup>
      </ComposableMap>
    
  );
};









const Mapp =(props) => {
    const [content, setContent] = useState("");
    return (
      <div className="Map"> 
        {MapChart(setContent , props.data)}
        <ReactTooltip>{content}</ReactTooltip>
      </div>
    );
  }









  
export default Mapp;
