import React from 'react';
import './App.css';

import Carddata  from './components/card-info/card-info-comp';
import Statstable from './components/stats-table/stats-table';
import Postandsoc from './components/posts and social/postSocial.comp'

class App extends React.Component {

  constructor(){
    super();
    this.state= "";
  }


  componentDidMount(){
    fetch('https://api.covid19api.com/summary')
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          gotinfo: true,
          data: result
        });
        }
    )};
      render(){
        const {gotinfo , data} = this.state;


          return (
          <div className="App">
            {
              gotinfo ? 
              <div className="contents">
                <Carddata head1="Global Cases" head2="Global Daily Increase" data={data.Global}/>
                <Statstable data={data.Countries}/> <section className="news-section"><Postandsoc />   </section>
                </div>

              : <p>Loading...</p>
            }

          </div>
        );
    }
}

export default App;
