import React from 'react';
import './App.css';

import Carddata  from './components/card-info/card-info-comp';
import getData from './data-src';


class App extends React.Component {

  constructor(){
    super()
    this.state = {
      gotinfo : false,
      data : ''

    }
  }


  componentDidMount(){
    const data = async ()=>{

      fetch('https://api.covid19api.com/summary')
      .then(res => res.json()).then(datta => {console.log(datta);
      this.setState(data = datta , gotinfo = true);
      })
      }

    }

  render(){


    return (
    <div className="App">

        <Carddata data={this.state.data} type={true} header="Global Numbers" />
        <Carddata data={this.state.data} type={false} header="Global Daily Increase" />
    </div>
  );}
}

export default App;
