import React from 'react';
import './App.css';

import Carddata  from './components/card-info/card-info-comp';
//import getData from './data-src';


class App extends React.Component {

  constructor(){
    super();
    this.state = {
      gotinfo : false,
      data : []
    }
  }


  componentDidMount(){
    fetch('https://api.covid19api.com/summary')
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          gotinfo: true,
          data: [result]
        });
        }
    )};
  render(){
    const {gotinfo , data} = this.state;

    return (
    <div className="App">
      {gotinfo ? <div>
               <Carddata data={data[0]} type={true} header="Global Numbers" />     
                  </div> 
          : <h1>hey</h1>}

    </div>
  );}
}

export default App;
