import React from 'react';
import './stats-table.scss'
class statsTable extends React.Component{
    constructor(){
        super();
        this.state={
            sortedby : '',
            itemPerpage: 10,
            noOfpages:10,
            page: 0
        } 
    }
    setPagenumber = (e) =>{
        var x = parseInt(e.target.options[e.target.options.selectedIndex].value)
        this.setState({itemPerpage : x,
                        noOfpages: Math.ceil(this.props.data.length/x),
                    page:0})
        }
    setItemsPerpage = (e)=>{
        console.log( e.target.getAttribute("data-index"))
        this.setState({page : e.target.getAttribute("data-index")})
    }
    render(){

        var {itemPerpage, noOfpages , page} = this.state;
        
        var pageNum = [];
        for(let i = 0; i<noOfpages;i++){
            pageNum.push(<li className="pagenum" key={i} data-index={i} onClick={this.setItemsPerpage}>
            {i+1}
            </li>)
        }
        return(
        <section className="table-stats">
            <table cellSpacing="0">
              <tr className="the-header">
                    <td className="index">&nbsp;&nbsp;</td>
                    <td className="tableh coun">Country   <span> <i className="fas fa-sort"></i></span></td>
                    <td className="tableh">Corona   <span> <i className="fas fa-sort"></i></span><br></br> Cases</td>
                    <td className="tableh">Deaths  <span> <i className="fas fa-sort"></i></span></td>
                    <td className="tableh">Recovered  <span> <i className="fas fa-sort"></i></span></td>
                </tr>
              
                    { this.props.data.slice(page*itemPerpage , (page*itemPerpage)+itemPerpage).map((country, index) =><tr key={index}>
                    <td>{page*noOfpages+index+1}</td>
                    <td>{country.Country}</td>
                    <td className="blue">{country.TotalConfirmed}</td>
                    <td  className="red">{country.TotalDeaths}</td>
                    <td  className="green">{country.TotalRecovered}</td>
                    </tr>)}
               
            </table>
             <ul className="pages-list">
                <li className="pagenum"><i className="fas fa-angle-left"></i></li>
                    {pageNum}
                 <li className="pagenum"><i className="fas fa-angle-right"></i></li>
                <li>
                    <select id="pagenumbers" onChange={this.setPagenumber}>
                        <option value="10">10/Page</option>
                         <option value="20">20/Page</option>
                         <option value="50">50/Page</option>
                         <option value="100">100/Page</option>
                    </select>
                </li>
            </ul>
         </section>
        )
    }  
}

export default statsTable;
