import React from 'react';
import './App.css';
import Header from './components/header/header.com';
import Homepage from './pages/homepage';
class App extends React.Component {

  constructor(){
    super();
    this.state= {
      gotinfo: false,
      data : ''
    }
  }


  async componentDidMount(){
    fetch('https://api.covid19api.com/summary')
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          gotinfo: true,
          data: result
        });
        }
    )

  };
      render(){
         const ind = this.state;

          return (
            <Route {...this.props} render={rest => {
              const success = (
                <div>
                  <Header />
                  <Homepage {...ind}/>
                </div>
              );

              const error = (
                <Redirect to={{
                  pathname: '/'
                }}/>
              );



              return ind.gotinfo ? success : error;
            }}/>

        );

    }
}

export default App;
