import React from 'react';
import './postSocial.style.scss';
import post_data from './postdata';
import Post from '../posts/post.comp';
import TweetEmbed from 'react-social-embed'
const postandS = (props)=>(
    <section className="news-section">{
    post_data.map((el ) => {
           
               return <Post key={el.id} className={props.className? 'post-n': 'post'} title={el.title} imgAlt={el.imgAlt} imgUrl={el.imguUrl} text={el.text.substr(0, 130)}/>
                {props.className ? <TweetEmbed url='https://twitter.com/WHO/status/1341776827342196742' />: null}
        })
    }
        <blockquote class="twitter-tweet"><p lang="en" dir="ltr">Live Q&amp;A on the new <a href="https://twitter.com/hashtag/COVID19?src=hash&amp;ref_src=twsrc%5Etfw">#COVID19</a> virus variant with WHO, @CovidGenomicUK &amp; <a href="https://twitter.com/sangerinstitute?ref_src=twsrc%5Etfw">@sangerinstitute</a> experts <a href="https://t.co/QoRQcmjCfj">https://t.co/QoRQcmjCfj</a></p>&mdash; World Health Organization (WHO) (@WHO) <a href="https://twitter.com/WHO/status/1341776827342196742?ref_src=twsrc%5Etfw">December 23, 2020</a></blockquote> <script async src="https://platform.twitter.com/widgets.js" charset="utf-8"></script>
       </section>
)

export default postandS;
