import React from 'react';
import './App.css';

import Carddata  from './components/card-info/card-info-comp';
//import getData from './data-src';


class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      gotinfo : false,
      data : []
    }
  }


  componentDidMount(){
    fetch('https://api.covid19api.com/summary')
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          gotinfo: true,
          data: result
        });
        }
    )};
  render(){
    const {gotinfo , data} = this.state;
    const datanew = Array.from(data);
    console.log(datanew)
    return (
    <div className="App">
      {gotinfo ? <div>
                    <p>{data.TotalConfirmed}</p>
                      {datanew.map(info =>{
                      {console.log(info)}
                      return(
                         <Carddata key="1" data={info}></Carddata>
                      )
                     })}

                  </div> 
          : <h1>hey</h1>}

    </div>
  );}
}

export default App;
