import React from 'react';
import './App.css';
import Header from './components/header/header.com';
import Homepage from './pages/homepage'
class App extends React.Component {

  constructor(){
    super();
    this.state= ""
  }


  componentDidMount(){
    fetch('https://api.covid19api.com/summary')
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          gotinfo: true,
          data: result
        });
        }
    )};
      render(){


          return (<div>
            <Header/>
            { this.state.gotinfo ? <Homepage gotinfo={this.state.gotinfo} data={this.state.data} ></Homepage> : <p className="Loading">Loading</p> }
          </div>

        );
    }
}

export default App;
