import React from 'react';
import './App.css';
import Header from './components/header/header.com';
import Homepage from './pages/homepage';
import { Switch , Route}  from 'react-router-dom';
class App extends React.Component {

  constructor(){
    super();
    this.state= ""
  }


  componentDidMount(){
    fetch('https://api.covid19api.com/summary')
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          gotinfo: true,
          data: result
        });
        }
    )};
      render(){


          return (<div>
            <Header/>

             <Switch>
                {this.state.gotinfo ? 
                <Route  path="/" component={Homepage( this.state.gotinfo, this.state.data)} /> 
                : <p className="Loading">Loading</p> } 
            </Switch>


          </div>

        );
    }
}

export default App;
