import React from 'react';
import './App.css';

import Carddata  from './components/card-info/card-info-comp';
//import getData from './data-src';


class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      gotinfo : false,
      data : []
    }
  }


  componentDidMount(){
    fetch('https://api.covid19api.com/summary')
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          gotinfo: true,
          data: result.Global
        });
        }
    )};
  render(){
    const {gotinfo , data} = this.state;
    const datanew = Array.from(data);
    console.log(datanew)
    return (
    <div className="App">
      {gotinfo ?   datanew.map(info =>{
            return(<div>
              <p>hi</p>

               <Carddata key="1" header="Global Cases" data={info}></Carddata>
               <Carddata key="2" header="Global Cases" data={info}></Carddata>
            </div>)
            }):<h1>yo</h1> }

    </div>
  );}
}

export default App;
