import React from 'react';
import './App.css';

import Carddata  from './components/card-info/card-info-comp';
//import getData from './data-src';


class App extends React.Component {

  constructor(){
    super();
    this.state = {
      gotinfo : false,
      data : ''
    }
  }


  componentDidMount(){
      fetch('https://api.covid19api.com/summary')
      .then(res => res.json())
      .then(datta => {console.log(datta[0].Global);
      //this.setState({data : datta[0].Global , gotinfo : true});
      })

    }

  render(){


    return (
    <div className="App">
      {this.state.gotinfo ? <div>
                  <Carddata data={this.state.data} type={true} header="Global Numbers" />
                  <Carddata data={this.state.data} type={false} header="Global Daily Increase" />
                  </div> 
          : <h1>hey</h1>}

    </div>
  );}
}

export default App;
