import React, { useState, memo  }from "react";
import ReactTooltip from "react-tooltip";
//import MapChart from "./map.comp";
import './map.comp.style.scss';
import {
  ZoomableGroup,
  ComposableMap,
  Geographies,
  Geography
} from "react-simple-maps";

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

  const max=(data)=>{
    let maxi=-1;
    for(let e of data){
      if(e>maxi) maxi=e;
    }
    return maxi;
  }






const MapChart = ( setTooltipContent ,props ) => {
  return (
    
      <ComposableMap data-tip="" projectionConfig={{ scale: 200 }}>
        <ZoomableGroup>
          <Geographies geography={geoUrl}>
            {({ geographies }) =>
              geographies.map(geo => {
               var value,index  ,maxi=max(props);
               for(let i=0;i<props.length;i++){
                 if(props[i].Country = geo.properties.NAME){
                  value = props[i].TotalConfirmed; index = i;
                  break; }
                 }
               
               return (<Geography
                  key={geo.rsmKey} data-try="yoy"
                  geography={geo}
                  onMouseEnter={() => {
                    const { NAME} = geo.properties;
                    setTooltipContent(`${NAME} — ${value.toLocaleString()}`);
                  }}
                  onMouseLeave={() => {
                    setTooltipContent("");
                  }}
                  style={{
                    default: {
                      fill: `rgba(209 , 7 , 0 , ${max/props[index].TotalConfirmed})`,
                      outline: "none"
                    },
                    hover: {
                      fill: "#F53",
                      outline: "none"
                    },
                    pressed: {
                      fill: "#E42",
                      outline: "none"
                    }
                  }}
                /> )
              })
            }
          </Geographies>
        </ZoomableGroup>
      </ComposableMap>
    
  );
};









const Mapp =(props) => {
    const [content, setContent] = useState("");
    return (
      <div className="Map"> 
        {MapChart(setContent , props.data)}
        <ReactTooltip>{content}</ReactTooltip>
      </div>
    );
  }









  
export default Mapp;
