import React from 'react';
import './App.css';
import Carddata  from './components/card-info/card-info-comp';
import Statstable from './components/stats-table/stats-table';
import Postandsoc from './components/posts and social/postSocial.comp';
import Mapp from './components/map/map.parent';

import Homepage from './pages/homepage'
class App extends React.Component {

  constructor(){
    super();
    this.state= {
      gotinfo: false,
      data :''
    }
  }


  componentDidMount(){
    fetch('https://api.covid19api.com/summary')
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          gotinfo: true,
          data: result
        });
        }
    )};
      render(){
        const {gotinfo , data} = this.state;


          return (<div>
            { gotinfo ? <Homepage gotinfo={gotinfo} data={data} /> : <p className="Loading">Loading</p> }
          </div>

        );
    }
}

export default App;
